import logo from './logo.svg';
import './App.css';
import React from 'react';


class Display extends React.Component {
	render() {
		return(
			<div className="display">

			</div>
		);
	}
}

class NumberSection extends React.Component {
	render() {
		return(
			<div className="numberSection">
				<button className="btnNumber" onClick={() => this.props.onClick()}>
					7
				</button>
				<button className="btnNumber" onClick={() => this.props.onClick()}>
					8
				</button>
				<button className="btnNumber" onClick={() => this.props.onClick()}>
					9
				</button>
				<button className="btnNumber" onClick={() => this.props.onClick()}>
					4
				</button>
				<button className="btnNumber" onClick={() => this.props.onClick()}>
					5
				</button>
				<button className="btnNumber" onClick={() => this.props.onClick()}>
					6
				</button>
				<button className="btnNumber" onClick={() => this.props.onClick()}>
					1
				</button>
				<button className="btnNumber" onClick={() => this.props.onClick()}>
					2
				</button>
				<button className="btnNumber" onClick={() => this.props.onClick()}>
					3
				</button>
				<button className="btnNumber" onClick={() => this.props.onClick()}>
					0
				</button>
				<button className="btnNumber" onClick={() => this.props.onClick()}>
					.
				</button>
			</div>
		);
	}
}

function OperatorSection(props) {
	return(
		<button className="btnOperator" onClick={props.onClick}>
			{props.value}
		</button>
	);
}

class Calculator extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			number: 0.0,
			operator: null,
		}
	}
	render() {
		return (
			<div className="calculatorBoard">
				<Display />
				<NumberSection
					onClick={() => alert('click')}
				/>
			</div>

		// <div className="App">
		//   <header className="App-header">
		//     <img src={logo} className="App-logo" alt="logo" />
		//     <p>
		//       Edit <code>src/App.js</code> and save to reload.
		//     </p>
		//     <a
		//       className="App-link"
		//       href="https://reactjs.org"
		//       target="_blank"
		//       rel="noopener noreferrer"
		//     >
		//       Learn React
		//     </a>
		//   </header>
		// </div>
		);
	}
}

export default Calculator;
