					6
				</button>
				<button className="btn" onClick={() => this.props.onClick()}>
					1
				</button>
				<button className="btn" onClick={() => this.props.onClick()}>
					2
				</button>
				<button className="btn" onClick={() => this.props.onClick()}>
					3
				</button>
				<button className="btn" onClick={() => this.props.onClick()}>
					0
				</button>
				<button className="btn" onClick={() => this.props.onClick()}>
					.
				</button>
			</div>
		);
	}
}

class OperatorSection extends React.Component {
	render() {
		return(
			<div className="operatorSection">
				<button className={`btn btnOp`} onClick={() => this.props.onClick()}>
					AC
				</button>
				<button className={`btn btnOp`} onClick={() => this.props.onClick()}>
					Del
				</button>
				<button className={`btn btnOp`} onClick={() => this.props.onClick()}>
					x
				</button>
				<button className={`btn btnOp`} onClick={() => this.props.onClick()}>
					/
				</button>
				<button className={`btn btnOp`} onClick={() => this.props.onClick()}>
					+
				</button>
				<button className={`btn btnOp`} onClick={() => this.props.onClick()}>
					-
				</button>
				<button className={`btn btnOp`} onClick={() => this.props.onClick()}>
					=
				</button>
			</div>
		);
	}
}

